sources.light-theme.scss:
@import "_mixins.scss";

$theme: light;
// $theme: dark;

body {
  display: flex;
}
div {
  width: 90px;
  height: 150px;
  margin-right: 15px;
  @include centerTextInBox;
  @if$theme == light {
    color: rgb(41, 39, 39)
  } @else if$theme == dark  {
    font-family: Cambria, Cochin, Georgia, Times, 'Times New Roman', serif;
    color: white;
    font-size: 20px;
    box-shadow: 5px 5px 15px 5px #000000;
    border-radius: 10px;
  }
}
.base {
  @if$theme == light {
    background-color: rgb(206, 206, 206);
  } @else if$theme == dark  {
    background-color: gray;
  }
}

.important {
   @if$theme == light {
    background-color: rgb(248, 195, 195);
  } @else if$theme == dark  {
    background-color: rgb(170, 73, 73);
  }
}

.warning {
    @if$theme == light {
    background-color: rgb(221, 236, 136);
  } @else if$theme == dark  {
    background-color: rgb(224, 241, 64);
  }
}

sources._mixins.scss:
@mixin centerTextInBox {
  display: flex;
  justify-content: center;
  align-items: center;
}
